import ResidentInput from "./ResidentInput.vue";
import ResidentList from './ResidentList.vue';
import axios from 'axios';
var baseUrl = 'http://localhost:5000';
export default {
  name: "ResidentSection",
  data: function data() {
    return {
      savedResidents: []
    };
  },
  props: {},
  components: {
    ResidentInput: ResidentInput,
    ResidentList: ResidentList
  },
  methods: {
    addResident: function addResident(resident) {
      axios.post(baseUrl + '/create-user', {
        name: resident
      }).then(this.getResidents());
    },
    removeResident: function removeResident($event) {
      axios.delete(baseUrl + '/delete-user', {
        data: {
          id: $event[0]
        }
      });
    },
    getResidents: function getResidents() {
      var _this = this;

      axios.get(baseUrl + '/users').then(function (res) {
        return _this.savedResidents = res.data;
      });
    }
  },
  mounted: function mounted() {
    this.getResidents();
  }
};
